import React, {useState, useEffect} from 'react'
import useAxios from '../utils/useAxios'
import Todo from './Todo'
import CreateTodo from './CreateTodo'

const TodosPage = () => {
    let [todos, setTodos] = useState([])
    const [todoRemaining, setTodoRemaining] = useState(0);

    let api = useAxios()

    useEffect(()=> {
        getTodos()
    }, [])


    var getTodos = async() =>{
        let response = await api.get('/api/')
        console.log(' ##### getTodos');
        if(response.status === 200){
            setTodos(response.data)
        }

    }

    useEffect(() => { setTodoRemaining(todos.filter(todo => !todo.completed).length) });


    const addTodo = value_args => {
        const newTodos = [...todos, { ...value_args, completed: false }];
        let response = api.post('/api/create/', {title: value_args.title, description: value_args.description})
        console.log(' ##### addTodo');
        getTodos()
    };

    const completeTodo = index => {
        const newTodos = [...todos];
        newTodos[index].completed = true;
        setTodos(newTodos);
    };

    const removeTodo = index => {
        const newTodos = [...todos];
        newTodos.splice(index, 1);
        setTodos(newTodos);
    };


    return (
        <div>
            <h1>Todo Page</h1>
            <div className="header">Pending todos ({todoRemaining})</div>
            <ul>
                {todos.map((todo, index) => (
                    <Todo key={todo.id} index={index} todo={todo} 
                    completeTodo={completeTodo}
                    removeTodo={removeTodo}
                    />
                ))}
            </ul>
            <div className="create-todo" >
                <CreateTodo addTodo={addTodo} />
            </div>
        </div>
    )
}

export default TodosPage
